mapper.select:
SELECT TRIM( REGEXP_REPLACE( REGEXP_REPLACE(ldnm_adrs, '\\\\[.*?\\\\]', ''), '\\\\(.*?\\\\)', '' ) ) AS address, COUNT(*) AS count FROM onbid_item WHERE ldnm_adrs IS NOT NULL AND ldnm_adrs <> ''
AND ldnm_adrs LIKE CONCAT('%', #{q}, '%')
GROUP BY address ORDER BY count DESC, address ASC LIMIT #{size} OFFSET #{offset}
